var member = {
    login: function(){
       this.close();
       art.dialog(
           {id:'member',title:'',fixed:true,content:template.render('tpl-member-login')}
       );
        cyzone.validform('form#member-login', function(json){
                if(json.code){
                    member.status();
                    $('body').append(json.data.synlogin);
                    art.dialog({id:'member'}).close();
                }else{
                        $('#loginbtn').attr('disabled', false);
                        cyzone.error(json.msg);
                }
                cyzone.del_masker();
        });
    },
    register: function(){
        this.close();
        art.dialog(
            {id:'member',title:'',fixed:true,content:template.render('tpl-member-register')}
        );
        cyzone.validform('form#member-register', function(json){
                cyzone.del_masker();
                if(json.code){
                    member.status();
                    $('body').append(json.data.synlogin);
                    art.dialog({id:'member'}).close();
                }else{
                        $('#registerbtn').attr('disabled', false);
                        cyzone.error(json.msg);
                }
        });

        $('.clause').click(function(event){
                var left = $(this).offset().left, top = $(this).offset().top, width = $(this).width(), height = $(this).height();
                $('#tk').show().css({
                        'left': left + 'px',
                        'top' : top + height + 'px'
                });
                $(document).one('click', function(){
                        $('#tk').hide();
                });
                event.stopPropagation();
        });
    },
    forgetpwd: function(){
        this.close();
        var dialog = art.dialog(
            {id:'member',title:'',fixed:true,content:template.render('tpl-member-forgetpwd-step1')}
        );
        cyzone.validform('#member-forgetpwd', function(json){
            cyzone.del_masker();
            if(json.code){
                dialog.content(template.render('tpl-member-forgetpwd-step2',json.data));
            }else{
                    $('#getpassbtn').attr('disabled', false);
                    cyzone.error(json.msg);
            }
        });
    },
    service: function(){
       art.dialog(
           {id:'service',title:'',fixed:true,content:template.render('tpl-member-service')}
       );
    },
    status: function(){
        if ($.cookie(COOKIE_PRE+'_userid')) {
            $('#login_status').hide();
            $('#logout_status').show();
            var username = $.cookie(COOKIE_PRE+'_username'),
                nickname = $.cookie(COOKIE_PRE+'_nickname');
            $('#logout_status em').html(nickname);
        } else {
            $('#logout_status').hide();
            $('#login_status').show();
        }
    },
    close: function(){
        var list = art.dialog.list;
        for (var i in list) {
            list[i].close();
        }
    },
    oauth: function(type) {
        window.authWin = window.open("/index.php?m=member&c=auth&a=init&close=1&type=" + type, "\u91cd\u65b0\u6388\u6743", "width=640,height=480,location=no,menubar=no,scrollbars=yes");
        var e = setInterval(function() {
            if(window.authWin.closed) {
                clearInterval(e);
                member.status();
                member.close();
            }
        }, 1E3);
    },

    addfavs: function(url){
        var fav = $('#fav');
        $.get(
            "/api.php?op=add_favorite&" + url,
            function(data){
                if(data == -1){
                    member.login();
                }else if(data == 2){
                    var favs = ($('#favs_num').text() * 1 - 1) > 0  ? ($('#favs_num').text() * 1 - 1) : '';
                    $('#favs_num').text(favs);
                    fav.removeClass('far_exit');
                    fav.addClass('far_add');
                }else if(data == 1){
                    $('#favs_num').text(($('#favs_num').text() * 1) + 1);
                    fav.removeClass('far_add');
                    fav.addClass('far_exit');
                }
            }
        );

    }
}

